/* Takes input on subType 24, calculates difference
   from last time, if around 60 seconds have passed
   since last report.
   It passes the value on as subType 17, and
   childSensorId 2 */

var uTime = Math.floor(Date.now()/60000);
var lastTime = context.get('lastTime')||0;
if (lastTime === 0) {
    context.set('lastTime', uTime);
    context.set('lastCount', Number(msg.payload));
} else {
    if (uTime != lastTime) {
        lastCount = context.get('lastCount');
        currentCount = Number(msg.payload);
        context.set('lastTime', uTime);
        context.set('lastCount', currentCount)
        msg.payload = currentCount - lastCount;
        return msg;
    }
}
return null

